import { useMemo, useReducer, createContext} from 'react'

const ToDoContext = createContext()

  function ToDoProvider({children}) {
    const todoContext = useMemo(
        () => ({
          addTodo: async (data) => {
            await fetch(
                "api/todo/create",
                {
                  method: "POST",
                  body: JSON.stringify(
                      {"name":"Something to do","isComplete":false}),
                }
              )
              dispatch({ type: "ADD" });
          },
        }),
        []
      );
    const [state, dispatch] = useReducer(
        (prevState, action) => {
          switch (action.type) {
            case "ADD":
              return {
                ...prevState,
                isLoading: true,
              };
          }
        },
        {
          isLoading: true,
          todos: [],
        }
      );
    const value = {state, dispatch}
    return <ToDoContext.Provider value={todoContext}>{children}</ToDoContext.Provider>
  }
  export {ToDoProvider}
