import React, { useState , useEffect, useContext} from 'react';
import ToDoContext from './context';
import { makeStyles } from '@material-ui/styles';
import {Button, Grid, Table, TableBody, TableCell, TableRow,TableHead
  ,FormControl, Paper,FormHelperText, Dialog,FormControlLabel, Switch,TextField,IconButton } from '@material-ui/core';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import CancelIcon from '@material-ui/icons/Cancel';
import EditIcon from '@material-ui/icons/Edit';
import AddTask from "./AddTask";

import Autocomplete from '@material-ui/lab/Autocomplete';

const useStyles = makeStyles(theme => ({
  paper: {
    width: 1000,
    textAlign: 'center',
    borderRadius: '10pt 10pt 4pt 4pt' 
 },
  header:{
    backgroundColor: '#ffffff',
    boxShadow: '0px 3px 6px #00000029',
    padding: '20px 24px 15px',
    fontWeight: '600', 
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderRadius: '10px 10px 0px 0px'
  },
  // grids:{
  //   lineHeight: 2
  // },
  helperText:{
    marginTop:"-62px"
  },
  button: {
    minWidth: "150px",
    fontWeight: 600,
    border: 2,
    boxShadow: '0px 3px 6px #00000029',
    borderRadius: '6px',
    margin: "21px"
  },
}));

const Content =  () => {
  const [open, setOpen] = React.useState(false);
  const {todoContext, state} = useContext(ToDoContext)
  useEffect( () => {

      fetch("api/todos", {
        headers : { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
         }})
        .then((response) => response.json())
        .then((json) => todoContext.listTodo(json.todos))
        // todoContext.setLoading(false);


  }, [state.isLoading])

  useEffect( () => {
    fetch("api/users", {
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }})
      .then((response) => response.json())
      .then((json) => todoContext.listUser(json.users))
  }, [])

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  console.log("users", state.userFcontext)
  const classes = useStyles();   



  const lsp = Object.values(state.todosFcontext);
  console.log("list",lsp)
return (
<div>
<Grid>
  <Paper className={classes.paper}> 
    <div className={classes.header}>
          <span>List</span>
    </div>
    <Grid container spacing={2} style={{ margin: "25px"}}>
       <Grid item xs={3}>
        <FormControl>

                  <TextField
                      label="Project Name" 
                      variant="outlined"
                      value={state.editFcontext.name}
                      fullWidth
                      size= "small" />


        </FormControl>
        </Grid>
        <Grid item xs={3}>
            <Autocomplete
                    id="user-field"
                    value={state.editFcontext.userId}
                    // freeSolo
                    options={state.userFcontext.map((user,key) => user.firstName + user.lastName ) }
                    renderInput={(params) => (
                      <TextField {...params} 
                          label="User" 
                          variant="outlined"

                          fullWidth
                          size= "small" />
                    )}
                  />
                          {/* <FormHelperText className={classes.helperText}>User</FormHelperText> */}
        </Grid>
        <Grid item xs={3}>
                    <FormControlLabel
                                value="completed"
                                size= "small"
                                control={<Switch color="primary" />}
                                />
                    <FormHelperText className={classes.helperText}>Completed</FormHelperText>      
                    </Grid>
    </Grid>
    <Paper style={{margin:"85px 25px 22px", border: "1px solid black"}}>
        <Table>

            <TableHead>

            <TableRow >
              <TableCell>Name </TableCell>
              <TableCell>User</TableCell>
              <TableCell>completed</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>

            </TableHead>

            <TableBody>
            {lsp.map(row =>
                    <TableRow >
                      <TableCell>{row.name} </TableCell>
                      <TableCell>{row.user} </TableCell>
                       <TableCell>{(row.isComplete= true)? <CheckCircleOutlineIcon/> : <HighlightOffIcon/> }</TableCell>

                      <TableCell><IconButton ><CancelIcon/></IconButton><IconButton  onClick={()=>todoContext.editTodo(row.id)}><EditIcon/></IconButton></TableCell> 
                    </TableRow>
            )}  
            </TableBody>
          </Table>


    </Paper>
    <Grid  container
                direction="row"
                justify="flex-end"
                alignItems="flex-end">
            <Button
                className={classes.button}
                variant="contained"
                color="primary"
                onClick={handleClickOpen}
            >
                Add Task
            </Button>
          </Grid>

  </Paper> 
  <Dialog aria-labelledby="max-width-dialog-title" open={open}
              style={{ backgroundColor: '#aef1eb54' }}
              PaperProps={{ classes: { root: classes.dialog } }}
              // onClose={handleClose}
        >
          <AddTask   handleClose={handleClose}/>
      </Dialog>  
  </Grid>
</div>
);
}
export default Content;
