import React, { useState, useMemo,useReducer } from 'react';
import logo from './logo.svg';
import './App.css'
import Header from "./Header";
import Content from "./Content";
import {makeServer} from "./server.js";
import ToDoContext from './context'

function App() {
  makeServer();


  const [todos,setTodos] = useState([])
  const [todo,setTodo] = useState("")

  const todoContext = useMemo(
    () => ({
      addTodo: async (data) => {
        await fetch(
            "api/todo/create",
            {
              method: "POST",
              body: JSON.stringify(
                {name:data.name, isComplete:data.isComplete}),
            }
          )
          dispatch({type: "LOADING", value:true});
      },

      listTodo: async (data) => {

          dispatch({ type: "LIST", value:data });
          console.log(state.todosFcontext)
      },

      listUser: async (data) => {

        dispatch({ type: "USER", value:data });
        console.log(state.userFcontext)
      },

      setIsLoading: async (data) => {

       dispatch({type: "LOADING", value:data});
      },
      editTodo: async (id) => {
        await fetch("api/todo/"+id, {
            headers : { 
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            }})
            .then((response) => response.json())
            .then((json) => dispatch({type: "EDIT", value:json.todo}))
      },

      deleteTodo: async (id) => {
        await fetch("api/todo/"+id+"/delete", {
            headers : { 
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            }})
            .then((response) => response.json())
            .then((json) => dispatch({type: "DELETE", value:json.todo}))
      },


    }),
    []
  );
  const [state, dispatch] = useReducer(
    (prevState, action) => {
      switch (action.type) {
        // case "ADD":
        //   return {
        //     ...prevState,
        //     isLoading: true,
        //   };
        case "LIST":
          return {
            ...prevState,
            todosFcontext: action.value,
          };
        case "USER":
        return {
          ...prevState,
          userFcontext: action.value,
        };
        case "LOADING":
        return {
          ...prevState,
          isLoading: action.value,
        };
        case "EDIT":
        return {
          ...prevState,
          editFcontext: action.value,
        };
        case "DELETE":
        return {
          ...prevState,
          deleteFcontext: action.value,
        };

      }
    },
    {
      isLoading: 0,
      todosFcontext: {},
      userFcontext:[],
      editFcontext:{},
      deleteFcontext:{}
    }
  );




  // useEffect(() => {
  //   fetch("api/users" , {
  //     headers : { 
  //       'Content-Type': 'application/json',
  //       'Accept': 'application/json'
  //      }
  //   })
  //     .then((result) => result.json())
  //     .then((resp) => {
  //       // console.warn(resp)
  //       setTodo(resp)
  //     })
  //   }
  // )

  return (
    <div className="App">
      <header className="App-header">
  <ToDoContext.Provider value={{todoContext, state, dispatch}}><Content /></ToDoContext.Provider>  

      </header>
    </div>
  );
}

export default App;
